CREATE TABLE Entry (Id NVARCHAR (64) UNIQUE NOT NULL, Filepath NVARCHAR (1024) NOT NULL);
CREATE TABLE Tag (Id NVARCHAR (64) UNIQUE NOT NULL, Name NVARCHAR (64) UNIQUE NOT NULL);
CREATE TABLE Tag_map (Id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,Tag_Id NVARCHAR (64) NOT NULL, Entry_Id NVARCHAR (64)  NOT NULL, UNIQUE (Tag_Id, Entry_Id), FOREIGN KEY (Tag_Id) REFERENCES Tag (Id),FOREIGN KEY (Entry_Id) REFERENCES Entry (Id));